INSERT INTO fact_industry_news (category_code, vendor_id, news_type, news_title, news_summary, news_date, source, relevance_score, ai_insight, action_items, dataset_id) VALUES
('mechanical', 'VEND001', 'Hot News', 'Global Mechanical Industries Announces Major Production Expansion', 'Leading mechanical equipment manufacturer announces $2.5B investment in new production facilities across Asia Pacific, expected to increase capacity by 35% by 2025.', '2024-01-15', 'Industry Today', 9.5, 'This expansion will significantly impact supply availability and may lead to more competitive pricing in the Asia Pacific region. Companies with ongoing contracts may benefit from improved delivery times.', ARRAY['Review contract terms to leverage improved capacity', 'Explore opportunities for volume discounts', 'Consider long-term agreements to secure favorable pricing'], 'DATASET_A'),
('mechanical', 'VEND004', 'Hot News', 'MechPower Unveils Revolutionary Energy-Efficient Technology', 'MechPower International launches next-generation equipment with 40% better energy efficiency, potentially disrupting market dynamics and setting new industry standards.', '2024-01-08', 'Tech Equipment Weekly', 9.8, 'This technology breakthrough positions MechPower as a potential strategic partner. Their non-contracted status presents an opportunity for first-mover advantage in securing partnership terms.', ARRAY['Schedule vendor assessment meeting', 'Evaluate technology fit for operations', 'Initiate contract discussions before competitors'], 'DATASET_A'),
('mechanical', NULL, 'Hot News', 'Global Steel Prices Surge 15% Amid Supply Constraints', 'Raw material costs for mechanical equipment manufacturers increase sharply due to supply chain disruptions and geopolitical tensions, expected to impact equipment prices in Q2 2024.', '2024-01-20', 'Commodities Monitor', 9.2, 'Price increases are imminent across the mechanical equipment category. Companies should act quickly to lock in current pricing through extended contracts or forward purchases.', ARRAY['Accelerate procurement timelines where possible', 'Negotiate price protection clauses', 'Consider strategic inventory buildup'], 'DATASET_A'),
('mechanical', 'VEND002', 'Other News', 'TechMech Solutions Wins Innovation Award', 'TechMech Solutions recognized for excellence in manufacturing innovation at the Global Industrial Awards 2024.', '2023-12-10', 'Industry Awards', 6.5, 'Positive brand recognition reinforces quality reputation of previously contracted vendor.', ARRAY['Monitor performance for potential re-engagement'], 'DATASET_A'),
('electrical', 'VEND011', 'Hot News', 'PowerElectric Secures Major Grid Modernization Contracts', 'PowerElectric Global wins $8.5B in contracts for smart grid infrastructure projects across North America, validating their market leadership and technology capabilities.', '2024-01-12', 'Energy Infrastructure News', 9.6, 'Contract wins demonstrate strong market confidence and financial stability. Current partnership positioning is favorable, but demand surge may impact delivery timelines.', ARRAY['Confirm delivery schedules for existing orders', 'Discuss capacity allocation priorities', 'Explore collaborative planning opportunities'], 'DATASET_A'),
('electrical', 'VEND014', 'Hot News', 'CircuitMasters Files Patents for Revolutionary Circuit Protection Technology', 'CircuitMasters announces breakthrough in circuit protection systems with 3 new patent filings, potentially reshaping safety standards in electrical equipment.', '2024-01-18', 'Patent & Innovation Report', 9.4, 'Patent portfolio strengthens CircuitMasters competitive position. As a non-contracted vendor with innovative technology, they represent an untapped opportunity for technology differentiation.', ARRAY['Conduct technology assessment', 'Evaluate patent implications for operations', 'Initiate vendor qualification process'], 'DATASET_A'),
('electrical', NULL, 'Hot News', 'New Safety Regulations to Impact Electrical Equipment by 2025', 'Global regulatory bodies announce stricter safety standards for electrical equipment, requiring manufacturers to meet enhanced certification requirements by Q3 2025.', '2024-01-05', 'Regulatory Watch', 9.7, 'Regulatory changes will create compliance costs and potential supply disruptions. Vendors with advanced compliance capabilities will have competitive advantage.', ARRAY['Audit vendor compliance readiness', 'Review equipment specifications vs new standards', 'Plan compliance transition strategy'], 'DATASET_A'),
('electrical', 'VEND012', 'Other News', 'ElectraTech Opens New R&D Center in Singapore', 'ElectraTech Systems invests in new research and development facility focused on next-generation power systems.', '2023-12-15', 'Business Expansion Weekly', 7.2, 'Continued investment in innovation supports long-term partnership value with current contracted vendor.', ARRAY['Explore collaborative innovation opportunities'], 'DATASET_A'),
('renewable', 'VEND021', 'Hot News', 'GreenPower Achieves 50% Cost Reduction in Solar Panel Production', 'GreenPower Solutions announces breakthrough manufacturing process that reduces solar panel production costs by 50%, promising significant price reductions for customers.', '2024-01-22', 'Renewable Energy Today', 9.9, 'Major cost breakthrough will reshape market pricing dynamics. Current contract holders should renegotiate terms to benefit from cost reductions. This strengthens GreenPower position as market leader.', ARRAY['Initiate contract amendment discussions', 'Quantify potential savings from new pricing', 'Explore expanded partnership scope'], 'DATASET_A'),
('renewable', 'VEND025', 'Hot News', 'Renewable Dynamics Secures $5B Investment for Asia Expansion', 'Major private equity investment enables Renewable Dynamics to triple production capacity and establish market presence in Southeast Asia renewable energy sector.', '2024-01-17', 'Investment News', 9.6, 'Significant capital injection transforms Renewable Dynamics from medium player to major competitor. Their non-contracted status and expansion trajectory make them attractive for strategic partnership.', ARRAY['Schedule executive meeting with Renewable Dynamics', 'Assess capabilities for strategic partnership', 'Evaluate geographic coverage benefits'], 'DATASET_A'),
('renewable', NULL, 'Hot News', 'Government Announces $50B Renewable Energy Infrastructure Program', 'Multi-year government initiative to accelerate renewable energy adoption creates unprecedented demand for solar, wind, and energy storage equipment.', '2024-01-10', 'Government Press Release', 9.8, 'Massive demand surge will strain supply chains and likely lead to extended lead times and price pressure. Early procurement and strategic vendor partnerships will be critical to secure supply.', ARRAY['Accelerate renewable equipment procurement plans', 'Secure long-term supply agreements', 'Identify additional qualified vendors'], 'DATASET_A'),
('renewable', 'VEND023', 'Other News', 'WindForce Wins Environmental Excellence Award', 'WindForce International recognized for sustainable manufacturing practices and environmental leadership in wind turbine production.', '2023-12-08', 'Environmental Awards', 6.8, 'Positive environmental credentials align with corporate sustainability goals.', ARRAY['Consider for future sustainability-focused procurements'], 'DATASET_A');
